#megapoll .list_1st{margin:10px 0 0;}
.list_1st li{position:relative;margin:5px 0 0;padding-left:10px;font-size:14px;line-height:20px;}
.list_1st li:before{content:"";display:block;position:absolute;top:8px;left:0;width:3px;height:3px;background-color:#444;}
