.ir{display:block;overflow:hidden;background:url(/kra/img/ir.png) no-repeat;text-indent:-9999px;}
.ir_hidden {display:none;overflow:hidden;background:url(/kra/img/ir.png) no-repeat;text-indent:-9999px;}
#visual{position:relative;/*height:600px;*/height:600px;background-repeat:no-repeat;background-position:50% 30%;background-size:cover;}
#visual .mask{position:absolute;top:0;left:0;width:100%;height:100%;background:url(/kra/img/mask-black-opacity25.png); background:none;}
.event_april{background-image:url(/kra/img/event_april.jpg);}
.event_june{background-image:url(http://letsrun-event.co.kr/wp-content/uploads/2017/12/렛츠런-리뉴얼이벤트페이지_이벤트종료-1.jpg);}
#visual .logo{position:absolute;top:20px;left:20px;width:220px;height:75px;background-position:-324px 0;z-index:2;}
.faq_header .logo{position:absolute;top:20px;left:20px;width:190px;height:66px;background-position:-544px 0;z-index:2;}
#visual .visual-left-word{position:absolute;top:80px;left:50%;margin-left:-450px;width:600px;z-index:3;}
#visual .animation-font{display:inline-block;position:relative;line-height:1;transition:0.25s top ease-in-out, 0.25s opacity ease-in-out;-webkit-transition:0.25s top ease-in-out, 0.25s opacity ease-in-out;opacity:0;}
#visual .animation-font.active{opacity:1;}
#visual .visual-left-word .animation-font{font-family:"Nanum Myeongjo";font-size:120px;line-height:1;}
#visual .animation-font.color-yellow{color:#fff496;}
#visual .animation-font.color-white{color:#fff;}
#visual .animation-font.active1{top:50px;transition-delay:200ms;}
#visual .animation-font.active2{top:100px;transition-delay:400ms;}
#visual .animation-font.active3{display:block;top:100px;left:40px;transition-delay:600ms;}
#visual .animation-font.active4{top:100px;margin-left:120px;transition-delay:800ms;}
#visual .animation-font.active5{top:150px;transition-delay:1000ms;}
#visual .animation-font.active6{top:50px;transition-delay:1750ms;}
#visual .animation-font.active.active1{top:0;}
#visual .animation-font.active.active2{top:50px;}
#visual .animation-font.active.active3{top:50px;}
#visual .animation-font.active.active4{top:50px;}
#visual .animation-font.active.active5{top:100px;}
#visual .animation-font.active.active6{top:0;}
#visual .visual-right-word{position:absolute;top:180px;right:50%;margin-right:-450px;width:650px;z-index:2;}
#visual .visual-right-word .animation-font{display:block;padding:15px 30px 15px 0;background:url(/kra/img/mask-pink-opacity70.png);color:#fff;font-size:30px;text-align:right;}
@media(max-width:1199px) {
	#visual{height:600px}
}
@media(max-width:1023px) {
	#visual{/*padding:75px 0 100px;height:auto;*/height:400px;}
	#visual .visual-left-word,
	#visual .visual-right-word{position:relative;top:auto;right:auto;left:auto;margin:0 auto;}
