#event_body #middle{margin-top:0;padding:120px 0 0;z-index:1;}
#event_body #middle .wrapper{width:100%;}
#event_body:before{content:"";display:block;position:absolute;top:0;left:0;width:100%;height:100%;background-image:url(/kra/img/event_body.png);background-repeat:repeat;background-position:50% top; background:none;}
/*#event_body:after{content:"";display:block;position:absolute;top:0;left:0;width:100%;height:100%;background-image:url(/kra/img/event_body_flower.png);}*/

#event_body .wrapper>p:first-child{margin-bottom:40px;font-size:40px;line-height:121px;text-align:center;}
#event_body .wrapper>p:first-child .underline{text-decoration:underline;}
#event_body .wrapper>p:first-child .color-pink{display:block;margin-top:5px;font-size:35px;line-height:40px;color:#396dc4}
#event_body .event-info{margin:0 auto 30px;width:420px;}
#event_body .event-info li{margin-top:15px;line-height:0;}
#event_body .event-info li:first-child{margin-top:0;padding:15px 0;background-color:#396dc4;border-radius:50px;color:#ffe09d;font-size:30px;line-height:40px;text-align:center;}
#event_body .event-info+p,
#event_body .event-info+p+p{color:#555;font-size:20px;line-height:25px;text-align:center;}
#event_body .event-info+p{margin-top:25px;}
.btn_wrap{margin-top:50px;text-align:center; overflow:hidden; width:660px; margin:70px auto 85px;}
.btn_wrap .btn{margin-top:10px;text-align:center; float:left;}
.btn_wrap .btn2{margin-top:10px;text-align:center; float:right;}
.btn_wrap .btn a{display:inline-block;padding:15px 25px;width:295px;background-color:#813630;border-radius:100px;color:#fff;font-size:32px;line-height:30px;}
.btn_wrap .btn2 a{display:inline-block;padding:15px 25px;width:295px;background-color:#fff;border-radius:100px;color:#813630;font-size:32px;line-height:30px; border:2px solid #813630;}

.snswrap{margin-top:30px;padding-bottom:30px;text-align:center;}
.snswrap li{display:inline-block;margin:0 30px;font-size:18px;text-align:center;}
.snswrap li a{display:block;line-height:22px;}
.snswrap li a .ir{margin:0 auto 10px;width:60px;height:60px;transition:0.25s transform ease-in-out;-webkit-transition:0.25s transform ease-in-out;}
.snswrap li a:hover .ir,
.snswrap li a:focus .ir{transform:rotate(45deg);}
.snswrap li a .bl{background-position:-180px -168px;}
.snswrap li a .fb{background-position:-240px -168px;}

@media (max-width:1023px) {
	#event_body .event-info{margin-bottom:30px;width:320px;}
	#event_body .event-info li{margin-top:10px;}
	#event_body .event-info li *{font-size:25px;line-height:35px;}
	#event_body .event-info li b{display:block;font-size:23px;}
	#event_body .wrapper>p:first-child{margin-bottom:30px;font-size:25px;line-height:25px;}
	#event_body .wrapper>p:first-child .color-pink{font-size:30px;line-height:35px;}
	#event_body .event-info+p,
	#event_body .event-info+p+p{font-size:18px;line-height:23px;}
	.btn_wrap{margin-top:35px;}
	.btn_wrap .btn a{padding:15px 25px;width:200px;font-size:18px;line-height:23px;}
}
@media (max-width:767px) {
	#event_body .event-info{width:300px;}
	#event_body .event-info li *{font-size:20px;line-height:40px;}
        #event_body .event-info li b{font-size:20px;line-height:25px;}
	#event_body .wrapper>p:first-child{font-size:20px;line-height:25px;}
	#event_body .wrapper>p:first-child .color-pink{font-size:25px;line-height:30px;}
	#event_body .event-info+p,
	#event_body .event-info+p+p{font-size:16px;line-height:21px;}









	.wrapper > * {font-family:'KoPub Dotum'; letter-spacing:-0.2px;}
