	.btn_wrap .btn{text-align:center; float:left;}
	.btn_wrap .btn2{text-align:center; float:right;}
	.btn_wrap .btn a{display:inline-block;padding:5px 15px;width:135px;background-color:#813630;border-radius:100px;color:#fff;font-size:15px;line-height:30px; border:2px solid #813630;}
	.btn_wrap .btn2 a{display:inline-block;padding:5px 15px;width:135px;background-color:#fff;border-radius:100px;color:#813630;font-size:15px;line-height:30px; border:2px solid #813630;}


	.lets_detail_wrap {background:url('http://letsrun-event.co.kr/wp-content/uploads/2017/10/patten_bg.png'); width:100%; clear:both; float:none;}
	.lets_detail {width:100%; margin:0 auto; padding:50px 0;}
	.lets_detail .lets_detail_txt {text-align:center; width:100%;}
	.lets_detail .lets_detail_txt .app_p1 {font-size:36px; color:#303030; padding-bottom:30px; line-height:25px; text-align:center;}
	.lets_detail .lets_detail_txt .app_p2 {font-size:18px; color:#303030; text-align:center;}
	.lets_detail .lets_detail_txt span.brown {color:#813630;}
	.lets_detail .lets_detail_txt span.uline {text-decoration:underline;}

	.lets_detail .lets_detail_img {padding-top:40px; width:100%; text-align:center; margin:auto;}
	.lets_detail .lets_detail_img a img {padding-right:10px;}
	.lets_detail .lets_detail_img a img.padding_none_mo {padding-right:0;}
	.lets_detail .lets_detail_img a img.padding_none {padding-right:0;}

	.lets_sns_wrap {background:#8c352e;}
	.lets_sns {width:100%; margin:auto; overflow:hidden; padding:30px 0;}
	.lets_sns .lets_sns1 {color:#fff; font-size:20px; width:100%; text-align:center; margin:auto; padding-bottom:30px;}
	.lets_sns .lets_sns1 .fshare {font-size:24px;}
	.lets_sns .lets_sns2 {width:100%; margin:auto; text-align:center;}
	.lets_sns .lets_sns2 a img {padding-left:10px;}
	.lets_sns .lets_sns2 a img.lets_sns2_img {padding-left:0;}
	.lets_sns .lets_sns3 {color:#fff; font-size:12px; width:100%; line-height:60px; font-family:'dotum','돋움'; width:100%; text-align:center; margin-top:20px;}















